import UIKit

protocol AddPickupBusinessLogic {
    func callAddPickupAPI(request: AddPickup.Request)
    func getSize()
}

protocol AddPickupDataStore {
    
}

class AddPickupInteractor: AddPickupBusinessLogic, AddPickupDataStore {
    var presenter: AddPickupPresentationLogic?
    var worker: AddPickupWorker?
    
    func callAddPickupAPI(request: AddPickup.Request) {
        worker = AddPickupWorker()
        worker?.createPickUp(APIRequest: request, completionHandler: { (response, message, successCode) in
            self.presenter?.presentAddPickUpResponse(message: message ?? "", successCode: successCode ?? "0")
        })
    }
    
    func getSize() {
         worker = AddPickupWorker()
        worker?.SizeList(completionHandler: { (response, message , success) in
            self.presenter?.presentSizeResponse(response: response, message: message ?? "", successCode: success ?? "0")
        })
    }
}
