import UIKit

class CancelPickupWorker {
    func cancelPickup(request: CancelPickup.Request, completionHandler: @escaping (_ message: String?, _ successCode: String?) -> Void) {
        NetworkService.dataRequest(with: MessageAPIRouter.cancelPickup(request: request), showLoader: true) { (responce: WSResponse<Settings.ViewModel>?, error: NetworkError?) in
            if let detail = responce {
                if let _ = detail.arrayData, let success = detail.setting?.isSuccess, let msg = detail.setting?.message, success {
                    completionHandler(msg, detail.setting?.success)
                } else {
                    completionHandler(detail.setting?.message, detail.setting?.success)
                }
            } else {
                completionHandler( error?.errorMessage() ?? AlertMessage.defaultError, "0")
            }
        }
    }
}
