import UIKit

protocol OngoingBusinessLogic {
    func ongoingRequest(request: Deliveries.Request, showLoader: Bool)
}

protocol OngoingDataStore {
}

class OngoingInteractor: OngoingBusinessLogic, OngoingDataStore {
    var presenter: OngoingPresentationLogic?
    var worker: OngoingWorker?
    
    func ongoingRequest(request: Deliveries.Request, showLoader: Bool) {
        worker = OngoingWorker()
        worker?.PickUpList(request: request, showLoader: showLoader, completionHandler: { (response, message, success) in
            self.presenter?.presentOngoingDeliveries(response: response, message: message ?? "", success: success ?? "0")
        })
    }
}
