import UIKit

protocol DriverProfileBusinessLogic {
    func driverDetail(request: DriverProfile.Request)
}

protocol DriverProfileDataStore {
    
}

class DriverProfileInteractor: DriverProfileBusinessLogic, DriverProfileDataStore {
    var presenter: DriverProfilePresentationLogic?
    var worker: DriverProfileWorker?
    
    func driverDetail(request: DriverProfile.Request) {
        worker = DriverProfileWorker()
        worker?.driverDetail(request: request, completionHandler: { (response, message, success) in
            self.presenter?.presentDriverDetail(response: response, message: message ?? "", success: success ?? "0")
        })
    }
}
