import UIKit

protocol ForgotPasswordPresentationLogic {
    func presentForgotPassword(response: [ForgotPassword.ViewModel]?,message: String, success: String)
}

class ForgotPasswordPresenter: ForgotPasswordPresentationLogic {
    
    weak var viewController: ForgotPasswordDisplayLogic?
    
    func presentForgotPassword(response: [ForgotPassword.ViewModel]?, message: String, success: String) {
        self.viewController?.didReceiveForgotPasswordResponse(response: response, message: message, success: success)
    }
}
