import UIKit

protocol OngoingDeliveriesBusinessLogic {
     func pickUpDetail(request: PickUpDetail.Request)
}

protocol OngoingDeliveriesDataStore {
    
}

class OngoingDeliveriesInteractor: OngoingDeliveriesBusinessLogic, OngoingDeliveriesDataStore {
    var presenter: OngoingDeliveriesPresentationLogic?
    var worker: OngoingDeliveriesWorker?
    
    func pickUpDetail(request: PickUpDetail.Request) {
        worker = OngoingDeliveriesWorker()
        worker?.details(request: request, completionHandler: { (response, message, success) in
            self.presenter?.presentDetails(response: response, message: message ?? "", success: success ?? "")
        })
    }
}
