import UIKit

protocol SettingsBusinessLogic {
    func logout()
    func deleteAccount()
}

protocol SettingsDataStore {
}

class SettingsInteractor: SettingsBusinessLogic, SettingsDataStore {
    var presenter: SettingsPresentationLogic?
    var worker: SettingsWorker?
    
    func logout() {
        worker = SettingsWorker()
        worker?.logout(completionHandler: { (message, success) in
            self.presenter?.presentLogout(message: message ?? "", success: success ?? "0" )
        })
    }
    
    func deleteAccount() {
        worker = SettingsWorker()
        worker?.deleteAccount(completionHandler: { (message, success) in
            self.presenter?.presentDeleteAccount(message: message ?? "", success: success ?? "0" )
        })
    }
}
