import UIKit

protocol SideMenuPresentationLogic {
    func presentLogout(message: String, success: String)
}

class SideMenuPresenter: SideMenuPresentationLogic {
    weak var viewController: SideMenuDisplayLogic?
    
    func presentLogout(message: String, success: String) {
        self.viewController?.didReceiveLogoutResponse(message: message, success: success)
    } 
}
