import UIKit

class VerifyOtpWorker {
    func signUp(request: SignUp.Request,completionHandler: @escaping (Login.Authentication.ViewModel?, _ message: String?, _ successCode: String?) -> Void) {
        NetworkService.dataRequest(with: LoginAPIRouter.signUp(request: request), showLoader: true) { (responce: WSResponse<Login.Authentication.ViewModel>?, error: NetworkError?) in
            if let detail = responce {
                if let resparray = detail.arrayData,resparray.count > 0, let success = detail.setting?.isSuccess, let msg = detail.setting?.message, success {
                    completionHandler( resparray.first, msg, detail.setting?.success)
                } else {
                    completionHandler(nil, detail.setting?.message, detail.setting?.success)
                }
            } else {
                completionHandler(nil, error?.errorMessage() ?? AlertMessage.defaultError, "0")
            }
        }
    }
    
    func changePhone(request: VerifyOtp.Request,completionHandler: @escaping ( _ message: String?, _ successCode: String?) -> Void) {
        NetworkService.dataRequest(with: EditProfileAPIRouter.changeMobileNumber(request: request), showLoader: true) { (responce: WSResponse<VerifyOtp.ViewModel>?, error: NetworkError?) in
            if let detail = responce {
                if let _ = detail.arrayData, let success = detail.setting?.isSuccess, let msg = detail.setting?.message, success {
                    completionHandler(msg, detail.setting?.success)
                } else {
                    completionHandler( detail.setting?.message, detail.setting?.success)
                }
            } else {
                completionHandler( error?.errorMessage() ?? AlertMessage.defaultError, "0")
            }
        }
    }
    
}
